function Demo(){
    const [count, setCount] = React.useState(0);

    function handleAdd(){
        //setCount(count+1); //setCount写法1: setXxx(newValue)
        setCount(count => count+1); //setCount写法2: setXxx(value => newValue)
    }

    return (
        <div>
            <h2>当前求和为：{count}</h2>
            <button onClick={this.handleAdd}>点击+1</button>
        </div>
    )
}

export default Demo;
